ee:message.ee:set-payload:
%dw 2.0
output application/json

var twitterBaseUri = p('secure::twitter.api.tweet.baseUri')

//build lower limit based on query parameter
var lowerTimeLimit = (now() >> "GMT") - ("PT" ++ (vars.queryParams.timeLimit) as String ++ "H") as Period

fun tweetIsWithinRange(inputDatetime) =
//check if the input datetime is between lowerTimeLimit and now
if(inputDatetime >= lowerTimeLimit and inputDatetime <= (now() >> "GMT")) true else false
---


{
	results: ( (
			payload.statuses 
			//filter tweets by time
			filter ((tweet, index) -> tweetIsWithinRange((tweet.created_at as DateTime {format: "EEE MMM dd HH:mm:ss Z yyyy"}) >> "GMT"))
		) 
		//limit the result to the number of tweets for the response
		[1 to (vars.queryParams.resultSize default p('secure::api.parameters.tweetSearchLimit.tweets'))]
		//build response body
		map ((item, index) -> 
		    {
		      text: item.text default "",
		      url: twitterBaseUri ++ (item.id_str default ""),
		      authorScreenName: item.user.screen_name default ""
		      //uncomment the below property to debug times in CST
//		      , createdAt: item.created_at as DateTime {format: "EEE MMM dd HH:mm:ss Z yyyy"} >> "CST"
		    }
		)) default []
}

ee:variables.ee:set-variable:
%dw 2.0
output application/java


---
([] + //check if resultSize is less than the maximum limit
(
	if((vars.queryParams.resultSize default 0) as Number > p('secure::api.parameters.tweetSearchLimit.tweets') as Number)
		("resultSize cannot be greater than " ++ p('secure::api.parameters.tweetSearchLimit.tweets'))
	else null
) + //check if searchCriteria is not an empty string
(
	if(vars.queryParams.searchCriteria == "")
		"searchCriteria cannot be blank"
	else null
) + //check if timeLimit is greater than 0
(
	if(vars.queryParams.timeLimit <= 0)
		"timeLimit must be greater than 0"
	else null
)) filter ((item, index) -> item != null)
